(48, 1, 0, 'shopOrderForm_new', '', 0, 8, 0, '[[!shkOptions?\n&get=`delivery,payments`\n&post_name=`shk_delivery,payment`\n&toPlaceholders=`1`\n&pl_prefix=`shkopt_`\n&tpl=`select_option`\n]]\n\n<p class="error">[[!+fi.error.error_message]]</p>\n<br />\n\n<form id="oformlenie" action="[[~[[*id]]]]" method="post">\n    <input type="text" name="nospam:blank" value="" style="display:none;" />\n    <input type="hidden" name="order" value="1" />\n\n	<label class="form-label" for="us-name">:</label><Br><input class="form-input" type="text" name="fullname" id="us-name" value="[[!+fi.fullname:default=`[[+modx.user.id:userinfo=`fullname`]]`:ne=`0`:show]]">\n	<div>[[!+fi.error.email]]</div><Br>\n	\n	<label class="form-label" for="us-adres"> :</label><Br><input name="address" class="form-input" type="text" id="us-adres" value="[[!+fi.address:default=`[[+modx.user.id:userinfo=`address`]]`:ne=`0`:show]]" />\n              <div>[[!+fi.error.address]]</div><Br>\n	<label class="form-label" for="us-mail">Email:</label><Br><input name="email" class="form-input" type="text" id="us-mail" value="[[!+fi.email:default=`[[+modx.user.id:userinfo=`email`]]`:ne=`0`:show]]" />\n            <div>[[!+fi.error.email]]</div><Br>\n	<label class="form-label" for="us-tel">:</label><Br><input name="phone" class="form-input" type="text" id="us-tel" value="[[!+fi.phone:default=`[[+modx.user.id:userinfo=`phone`]]`:ne=`0`:show]]" />\n            <div>[[!+fi.error.phone]]</div><Br>\n\n	<label class="form-label" for="us-sub"> :</label><Br>\n    <select name="shk_delivery" class="form-select" id="us-sub">\n	    <option value=""></option>\n        [[!+shkopt_delivery]]\n	</select><Br>\n    <div>[[!+fi.error.shk_delivery]]</div>\n    \n	<label class="form-label" for="us-sub"> :</label><Br>\n	<select name="payment" class="form-select" id="us-sub">\n	    <option value=""></option>\n        [[!+shkopt_payments]]\n	</select>\n    <div>[[!+fi.error.payment]]</div>\n    \n	<div class="ofor-div">\n		<label class="form