(14, 1, 0, 'tmRefresh', 'Refresh tagManager filters data.', 0, 0, 0, '/**\n * tmRefresh\n *\n * System event: OnDocFormSave, OnCacheUpdate\n *\n */\n\n$eventName = $modx->event->name;\n\nif( !function_exists( ''get_filter_categories'' ) ){\n    \n    function get_filter_categories(){\n        \n        global $modx;\n        \n        $modelpath = $modx->config[''core_path''].''components/tag_manager2/model/'';\n        $modx->addPackage(''tag_manager2'', $modelpath);\n        \n        $f_categories = array();\n        $c = $modx->newQuery("tagManager");\n        $c->select(array(''id'',''category''));\n        $c->groupby(''category'');\n        if ($c->prepare() && $c->stmt->execute()) {\n            \n            while ($row = $c->stmt->fetch(PDO::FETCH_ASSOC)) {\n                \n                array_push( $f_categories, $row[''category''] );\n                \n            }\n            \n        }\n        \n        return $f_categories;\n        \n    }\n    \n}\n\nif( $eventName == ''OnDocFormSave'' ){\n\n    $page_id = $resource->get(''id'');\n    $page_parent = $resource->get(''parent'');\n    $isfolder = $resource->get(''isfolder'');\n    $catalog_root = $modx->getOption(''tag_mgr2.catalog_id'', null, 0);\n    \n    if( !$isfolder ){\n        \n        $f_categories = get_filter_categories();\n        \n        if( in_array( $page_parent, $f_categories ) ){\n            \n            $response = $modx->runProcessor(''updatefilters'',\n                array(\n                   ''parent_id'' => $page_parent,\n                   ''clear_cache'' => false\n                ),\n                array(''processors_path'' => $modx->config[''core_path''] . ''components/tag_manager2/processors/mgr/'')\n            );\n            if ($response->isError()) {\n                return $response->getMessage();\n            }\n            \n        }\n        \n    }\n    \n}else if( $eventName == ''OnCacheUpdate'' ){\n    \n    //run in background\n    @exec( "php -f ''" . $modx->config[''assets_path''] . "components/tag_manager2/cron_updatefilters.php'' > /dev/null 2>&1 &" );\n    \n    return ''tmRefresh: OK'';\n    \n}\n\n//$modx->event->output('''');', 0, 'a:0:{}', 1, '', 1, 'core/components/tag_manager2/elements/plugins/plugin.tm_refresh.php'),
(25, 0, 0, 'SimpleSearchIndexer', 'Automatically indexes Resources into Solr.', 0, 0, 0, '/**\n * SimpleSearch\n *\n * Copyright 2010-11 by Shaun McCormick <shaun+sisea@modx.com>\n *\n * This file is part of SimpleSearch, a simple search component for MODx\n * Revolution. It is loosely based off of AjaxSearch for MODx Evolution by\n * coroico/kylej, minus the ajax.\n *\n * SimpleSearch is free software; you can redistribute it and/or modify it under\n * the terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * SimpleSearch is distributed in the hope that it will be useful, but WITHOUT\n * ANY WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS\n * FOR A PARTICULAR PURPOSE. See the GNU General Public License for more\n * details.\n *\n * You should have received a copy of the GNU General Public License along with\n * SimpleSearch; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package simplesearch\n */\n/**\n * Plugin to index Resources whenever they are changed, published, unpublished,\n * deleted, or undeleted.\n *\n * @var modX $modx\n * @var SimpleSearch $search\n *\n * @package simplesearch\n */\n\nrequire_once $modx->getOption(''sisea.core_path'',null,$modx->getOption(''core_path'').''components/simplesearch/'').''model/simplesearch/simplesearch.class.php'';\n$search = new SimpleSearch($modx,$scriptProperties);\n\n$search->loadDriver($scriptProperties);\nif (!$search->driver || (!($search->driver instanceof SimpleSearchDriverSolr) && !($search->driver instanceof SimpleSearchDriverElastic))) return;\n\n/**\n * helper method for missing params in events\n * @param modX $modx\n * @param array $children\n * @param int $parent\n * @return boolean\n */\nif (!function_exists(''SimpleSearchGetChildren'')) {\n    function SimpleSearchGetChildren(&$modx,&$children,$parent) {\n        $success = false;\n        $kids = $modx->getCollection(''modResource'',array(\n            ''parent'' => $parent,\n        ));\n        if (!empty($kids)) {\n            /** @var modResource $kid */\n            foreach ($kids as $kid) {\n                $children[] = $kid->toArray();\n                SimpleSearchGetChildren($modx,$children,$kid->get(''id''));\n            }\n        }\n        return $success;\n    }\n}\n\n$action = ''index'';\n$resourcesToIndex = array();\nswitch ($modx->event->name) {\n    case ''OnDocFormSave'':\n        $action = ''index'';\n        $resourceArray = $scriptProperties[''resource'']->toArray();\n\n        if ($resourceArray[''published''] == 1 && $resourceArray[''deleted''] == 0) {\n            $action = ''index'';\n            foreach ($_POST as $k => $v) {\n                if (substr($k,0,2) == ''tv'') {\n                    $id = str_replace(''tv'','''',$k);\n                    /** @var modTemplateVar $tv */\n                    $tv = $modx->getObject(''modTemplateVar'',$id);\n                    if ($tv) {\n                        $resourceArray[$tv->get(''name'')] = $tv->renderOutput($resource->get(''id''));\n                        $modx->log(modX::LOG_LEVEL_DEBUG,''Indexing ''.$tv->get(''name'').'': ''.$resourceArray[$tv->get(''name'')]);\n                    }\n                    unset($resourceArray[$k]);\n                }\n            }\n        } else {\n            $action = ''removeIndex'';\n        }\n\n        unset($resourceArray[''ta''],$resourceArray[''action''],$resourceArray[''tiny_toggle''],$resourceArray[''HTTP_MODAUTH''],$resourceArray[''modx-ab-stay''],$resourceArray[''resource_groups'']);\n        $resourcesToIndex[] = $resourceArray;\n        break;\n    case ''OnDocPublished'':\n        $action = ''index'';\n        $resourceArray = $scriptProperties[''resource'']->toArray();\n        unset($resourceArray[''ta''],$resourceArray[''action''],$resourceArray[''tiny_toggle''],$resourceArray[''HTTP_MODAUTH''],$resourceArray[''modx-ab-stay''],$resourceArray[''resource_groups'']);\n        $resourcesToIndex[] = $resourceArray;\n        break;\n    case ''OnDocUnpublished'':\n    case ''OnDocUnPublished'':\n        $action = ''removeIndex'';\n        $resourceArray = $scriptProperties[''resource'']->toArray();\n        unset($resourceArray[''ta''],$resourceArray[''action''],$resourceArray[''tiny_toggle''],$resourceArray[''HTTP_MODAUTH''],$resourceArray[''modx-ab-stay''],$resourceArray[''resource_groups'']);\n        $resourcesToIndex[] = $resourceArray;\n        break;\n    case ''OnResourceDuplicate'':\n        $action = ''index'';\n        /** @var modResource $newResource */\n        $resourcesToIndex[] = $newResource->toArray();\n        $children = array();\n        SimpleSearchGetChildren($modx,$children,$newResource->get(''id''));\n        foreach ($children as $child) {\n            $resourcesToIndex[] = $child;\n        }\n        break;\n    case ''OnResourceDelete'':\n        $action = ''removeIndex'';\n        $resourcesToIndex[] = $resource->toArray();\n        $children = array();\n        SimpleSearchGetChildren($modx,$children,$resource->get(''id''));\n        foreach ($children as $child) {\n            $resourcesToIndex[] = $child;\n        }\n        break;\n    case ''OnResourceUndelete'':\n        $action = ''index'';\n        $resourcesToIndex[] = $resource->toArray();\n        $children = array();\n        SimpleSearchGetChildren($modx,$children,$resource->get(''id''));\n        foreach ($children as $child) {\n            $resourcesToIndex[] = $child;\n        }\n        break;\n}\n\nforeach ($resourcesToIndex as $resourceArray) {\n    if (!empty($resourceArray[''id''])) {\n        if ($action == ''index'') {\n            $search->driver->index($resourceArray);\n        } else if ($action == ''removeIndex'') {\n            $search->driver->removeIndex($resourceArray[''id'']);\n        }\n    }\n}\nreturn;', 0, NULL, 0, '', 0, '');
