(1, 1, 0, 'tpl.AjaxForm.example', '', 0, 2, 0, '<form action="" method="post" class="ajax_form af_example">\n\n    <div class="form-group">\n        <label class="control-label" for="af_name">[[%af_label_name]]</label>\n        <div class="controls">\n            <input type="text" id="af_name" name="name" value="[[+fi.name]]" placeholder="" class="form-control"/>\n            <span class="error_name">[[+fi.error.name]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <label class="control-label" for="af_email">[[%af_label_email]]</label>\n        <div class="controls">\n            <input type="email" id="af_email" name="email" value="[[+fi.email]]" placeholder="" class="form-control"/>\n            <span class="error_email">[[+fi.error.email]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <label class="control-label" for="af_message">[[%af_label_message]]</label>\n        <div class="controls">\n            <textarea id="af_message" name="message" class="form-control" rows="5">[[+fi.message]]</textarea>\n            <span class="error_message">[[+fi.error.message]]</span>\n        </div>\n    </div>\n\n    <div class="form-group">\n        <div class="controls">\n            <button type="reset" class="btn btn-default">[[%af_reset]]</button>\n            <button type="submit" class="btn btn-primary">[[%af_submit]]</button>\n        </div>\n    </div>\n\n    [[+fi.success:is=`1`:then=`\n    <div class="alert alert-success">[[+fi.successMessage]]</div>\n    `]]\n    [[+fi.validation_error:is=`1`:then=`\n    <div class="alert alert-danger">[[+fi.validation_error_message]]</div>\n    `]]\n</form>', 0, NULL, 0, 'core/components/ajaxform/elements/chunks/chunk.example.tpl'),
(2, 0, 0, 'head', '', 0, 4, 0, '<!DOCTYPE HTML>\n<html>\n<head>\n[[!MetaX?\n]]\n    <link rel="stylesheet" type="text/css" href="/assets/shop/css/style.css" />\n    <link href="/assets/web/css/style_2.css" rel="stylesheet" type="text/css"  media="all" />\n    <link href=''http://fonts.googleapis.com/css?family=Roboto'' rel=''stylesheet'' type=''text/css''>\n	<link rel="stylesheet" href="/assets/web/css/flexslider.css" type="text/css" media="all" />\n	<link href=''http://fonts.googleapis.com/css?family=Ruslan+Display&subset=latin,cyrillic'' rel=''stylesheet'' type=''text/css''>\n	<script src="/assets/web/js/modernizr.js"></script>\n	<script src="/assets/web/js/responsive-nav.js"></script>\n    \n	<!--[if IE]>\n	<script src="http://html5shiv.googlecode.com/svn/trunk/html5.js"></script>\n	<link rel="stylesheet" type="text/css" href="/assets/shop/css/ie.css" />\n	<![endif]-->\n\n	<!--<script src="http://ajax.googleapis.com/ajax/libs/jquery/1.6.2/jquery.min.js"></script>\n	<script type="text/javascript" src="/assets/shop/js/jquery-3.1.1.min.js"></script>-->\n	<script type="text/javascript" src="/assets/shop/js/jquery.js"></script>\n	<script type="text/javascript" src="/assets/shop/js/script.js"></script>\n	<script type="text/javascript" src="/assets/shop/js/myscript.js"></script>\n	<script type="text/javascript" src="/assets/shop/js/jquery.colorbox-min.js"></script>\n	<script type="text/javascript" src="/assets/shop/js/jquery.toastmessage.js"></script>\n	<link href="/assets/shop/css/jquery-ui.css" rel="stylesheet">\n	<link href="/assets/shop/css/colorbox.css" rel="stylesheet">\n	<link href="/assets/shop/css/jquery.toastmessage.css" rel="stylesheet">\n	<script type="text/javascript" src="/assets/shop/js/jquery-ui.min.js"></script>\n</head>\n<body>\n\n		<div id="wrap">', 0, 'a:0:{}', 0, ''),
(3, 0, 0, 'footer', '', 0, 4, 0, '<div class="clear"></div>\n</div>	<!--end content-->\n\n\n<div class="footer"> \n		<div class="wrap"> \n		<div class="left-footer"> \n			<a href="[[~1]]"><img src="/assets/web/images/logo1.png" title="logo"></a>\n		</div>\n		<div class="right-footer">\n			<p>Autozone  &#169	 All Rights Reserved</p>\n		</div>\n		<div class="clear"> </div>\n	</div>\n	\n</div>\n</div><!--end wrap-->\n	</body>\n</html>', 0, 'a:0:{}', 0, ''),
(4, 0, 0, 'header', '', 0, 4, 0, '<header>\n<!--start-logo-->\n<div class="logo">\n	<a href="[[~1]]"><img src="/assets/web/images/logo.png" /></a>\n</div>\n    \n    <div id="header-block2">\n		<span>Телефон:</span><br>\n		<span class="phone">+7 (495)</span> 540 28 48\n	</div><!--end header-block2-->\n    \n	<div id="header-block3">\n		<a href="#" onclick="jQuery(''#hover-window-wrap'').show(); return false" class="call-button">Заказать звонок</a>\n	</div><!--end header-block3-->\n\n	    <div id="header-block4">\n            [[!SimpleSearchForm?\n            	&tpl=`SearchFormTpl`\n            	&landing=`43`\n            ]]\n    </div><!--end header-block4-->\n    \n    <ul id="second-menu">\n[[!Shopkeeper3@cart_catalog]]\n    [[![[*id:is=`1`:then=`-`]]compare?\n        &comparePageId=`28`\n        &limitProducts=`4`\n        &toCompare_tpl=`compare`\n        &onlyThisParentId=`[[*id]]`\n    ]]\n\n\n    	\n    	<li id="sm-shop">\n    		<a href="#" onclick="return false">Вход в кабинет</a>\n    \n    		<div id="enter-widow">\n                [[!Personalize? &yesChunk=`header_for_members` &noChunk=`header_for_guests` &ph=`name`]]\n    		</div>\n    	</li>\n    </ul>\n    \n	<!--<div id="header-block1">\n		<span>Бесплатный звонок:</span><br>\n		<span class="phone">8 800</span> 540 28 48\n	</div>end header-block1-->\n</header>\n<!--start-header-->\n<div class="header">\n<!--start-wrap-->\n<div class="wrap">\n	<!-- Главное меню -->\n   	<div class="top-nav">\n       	[[pdoMenu?\n        	&level=`4`\n        	&parents=`0`\n        	&resources=`-4`\n        	&displayStart=`1`\n        	&scheme=`full`\n        	&tplOuter=`@INLINE <ul>[[+wrapper]]</ul>`\n        	&tpl=`@INLINE <li><a href="[[+link]]" [[+attributes]]>[[+menutitle]]</a>[[+wrapper]]</li>`\n        	&tplHere=`@INLINE <li class="active"><a href="[[+link]]" [[+attributes]]>[[+menutitle]]</a>[[+wrapper]]</li>`\n        	&tplParentRow=`@INLINE <li><a href="[[+link]]" [[+attributes]]>[[+menutitle]]</a><ul>[[+wrapper]]</ul></li>`\n        	&tplInner=`@INLINE <li>[[+wrapper]]</li>`\n        	&tplParentRowHere=`@INLINE <li class="active"><a href="[[+link]]" [[+attributes]]>[[+menutitle]]</a><ul>[[+wrapper]]</ul></li>`\n        ]]\n    </div>\n	<!-- end Главное меню -->\n<div class="clear"> </div>\n				<!--End-wrap-->\n			</div>\n			<!--End-header-->\n		</div>\n				<div class="clear"> </div>\n<div id="content">\n\n<div class="clear"> </div>', 0, 'a:0:{}', 0, ''),
(55, 0, 0, 'foto_slider', '', 0, 4, 0, '<li><img src="[[+image]]" width="895" height="362" alt="[[+set]]"/></li>\n<h3 style="display:none;">[[+set]]</h3>\n<h2 style="display:none;">[[+word]]</h2>\n<p style="display:none;">[[+description]]</p>\n<a href="[[+link]]" style="display:none;"><button type="button" class="btn-warning">Перейти</button></a>', 0, 'a:0:{}', 0, ''),
(56, 1, 0, 'toCompare', '', 0, 19, 0, '<p>\n    Выбрано <span id="skolko_vibrano">[[+count_current]]</span> из [[+count_max]]\n    / <a href="[[+href_compare]]" onclick="return shkCompare.toCompareLink();">сравнить</a>\n    <span id="sravnenie_otmena" style="display:[[+display_cancel]];">\n    /\n    <a href="[[+href_cancel]]">отменить</a></span>\n</p>\n<br clear="all" />', 0, NULL, 0, ''),
(7, 0, 0, 'slider', '', 0, 4, 0, '<aside>\n    \n    	<div class="banner-withtext" id="banner-slider">\n        	<a>\n        	    <h3>ACHIEVE THE HIGHEST</h3>\n            	<h2>RESULTS</h2>\n            	<P>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,</P>\n        	</a>\n        	\n        	<!--<a href="" class="pos-absolute"><button type="button" class="btn-warning">Перейти</button></a>-->\n        </div>			\n</aside>\n<div id="container">\n	<div id="slider">\n		<ul>\n		    [[getImageList?\n                &tvname=`galleryPhotos`\n                &tpl=`foto_slider`\n                &limit=`4`\n                &docid=`[[*id]]`\n             ]]\n		</ul>\n	</div>\n	<ul id="slide-pagination" class="slide-pagination">\n		<li onclick="slideshow.pos(0)"></li>\n		<li onclick="slideshow.pos(1)"></li>\n		<li onclick="slideshow.pos(2)"></li>\n		<li onclick="slideshow.pos(3)"></li>\n	</ul>\n</div><!--container-->\n	\n	\n	<script type="text/javascript">\n	var slideshow=new TINY.slider.slide(''slideshow'',{\n		id:''slider'',\n		auto:4,\n		resume:false,\n		vertical:false,\n		navid:''slide-pagination'',\n		activeclass:''current'',\n		position:0,\n		rewind:false,\n		elastic:true\n	});\n	</script>\n<div class="clear"></div>', 0, 'a:0:{}', 0, ''),
(8, 0, 0, 'articles', '', 0, 4, 0, '<div class="col images_1_of_5">\n        [[getImageList?\n                &tvname=`galleryPhotos`\n                &tpl=`thumbTpl_article`\n                &limit=`1`\n                &docid=`[[+id]]`\n          ]]\n		 <h2>[[+pagetitle]]</h2>\n		 <p>[[+description]]</p>\n	     <div class="button"><span><a href="[[~[[+id]]]]">Подробнее</a></span></div>\n</div>', 0, 'a:0:{}', 0, ''),
