:5:"value";s:6:"option";s:7:"lexicon";s:17:"formit:properties";s:4:"area";s:0:"";}s:7:"useAbbr";a:7:{s:4:"name";s:7:"useAbbr";s:4:"desc";s:22:"prop_fiso.useabbr_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";s:17:"formit:properties";s:4:"area";s:0:"";}s:17:"selectedAttribute";a:7:{s:4:"name";s:17:"selectedAttribute";s:4:"desc";s:32:"prop_fiso.selectedattribute_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:20:" selected="selected"";s:7:"lexicon";s:17:"formit:properties";s:4:"area";s:0:"";}s:13:"toPlaceholder";a:7:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:28:"prop_fiso.toplaceholder_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:17:"formit:properties";s:4:"area";s:0:"";}s:7:"country";a:7:{s:4:"name";s:7:"country";s:4:"desc";s:22:"prop_fiso.country_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:16:"[[++cultureKey]]";s:7:"lexicon";s:17:"formit:properties";s:4:"area";s:0:"";}}', '', 0, ''),
(19, 0, 0, 'FormItSaveForm', 'Custom hook for FormIt to save the form.', 0, 3, 0, '/**\r\n * FormIt\r\n *\r\n * Copyright 2011-12 by SCHERP Ontwikkeling <info@scherpontwikkeling.nl>\r\n * Copyright 2015 by Wieger Sloot <modx@sterc.nl>\r\n *\r\n * FormIt is free software; you can redistribute it and/or modify it\r\n * under the terms of the GNU General Public License as published by the Free\r\n * Software Foundation; either version 2 of the License, or (at your option) any\r\n * later version.\r\n *\r\n * FormIt is distributed in the hope that it will be useful, but WITHOUT ANY\r\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\r\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\r\n *\r\n * You should have received a copy of the GNU General Public License along with\r\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\r\n * Suite 330, Boston, MA 02111-1307 USA\r\n *\r\n * @package formit\r\n */\r\n/**\r\n * A custom FormIt hook for saving filled-in forms. - Based on FormSave\r\n *\r\n * @var modX $modx\r\n * @var array $scriptProperties\r\n * @var FormIt $formit\r\n * @var fiHooks $hook\r\n * \r\n * @package formit\r\n */\r\n/* setup default properties */\r\n$values = $hook->getValues();\r\n$formName = $modx->getOption(''formName'', $formit->config, ''form-''.$modx->resource->get(''id''));\r\n// process formName. Pick a value from the form\r\n// Inspired from the email''s hook of formit (fihooks.class.php)\r\nif (is_string($formName)) {\r\n    foreach ($fields as $k => $v) {\r\n        if (is_scalar($k) && is_scalar($v)) {\r\n            $formName = str_replace(''[[+''.$k.'']]'',$v,$formName);\r\n        }\r\n    }\r\n}\r\n\r\n$formEncrypt = $modx->getOption(''formEncrypt'', $formit->config, false);\r\n$formFields = $modx->getOption(''formFields'', $formit->config, false);\r\n$fieldNames = $modx->getOption(''fieldNames'', $formit->config, false);\r\n$updateSavedForm = $modx->getOption(''updateSavedForm'', $formit->config, false); // true, false, ''1'', ''0'', or ''values''\r\n// In order to use update process, you need to provide the hash key to the user somehow\r\n// Usually with [[!FormItRetriever]] to populate this form field:\r\n$formHashKeyField = $modx->getOption(''savedFormHashKeyField'', $formit->config, ''savedFormHashKey'');\r\n// Disable if you want to use the session_id() in your hash, like FormIt does\r\n// WARNING: this can cause potential hash key collisions and overwriting of the wrong form record!!\r\n$formHashKeyRandom = $modx->getOption(''formHashKeyRandom'', $formit->config, true);\r\n// process formHashKeyField into variable for later use\r\n$formHashKey = (isset($values[$formHashKeyField])) ? (string) $values[$formHashKeyField] : '''';\r\n// our hashing methods return 32 chars\r\nif (strlen($formHashKey) !== 32) $formHashKey = '''';\r\nunset($values[$formHashKeyField]);\r\n\r\nif ($formFields) {\r\n    $formFields = explode('','', $formFields);\r\n    foreach($formFields as $k => $v) {\r\n        $formFields[$k] = trim($v);\r\n    }\r\n}\r\n// Build the data array\r\n$dataArray = array();\r\nif($formFields){\r\n    foreach($formFields as $field) {\r\n        $dataArray[$field] = (!isset($values[$field])) ? '''' : $values[$field];\r\n    }\r\n}else{\r\n    $dataArray = $values;\r\n}\r\n//Change the fieldnames\r\nif($fieldNames){\r\n    $newDataArray = array();\r\n    $fieldLabels = array();\r\n    $formFieldNames = explode('','', $fieldNames);\r\n    foreach($formFieldNames as $formFieldName){\r\n        list($name, $label) = explode(''=='', $formFieldName);\r\n        $fieldLabels[trim($name)] = trim($label);\r\n    }\r\n    foreach ($dataArray as $key => $value) {\r\n        if($fieldLabels[$key]){\r\n            $newDataArray[$fieldLabels[$key]] = $value;\r\n        }else{\r\n            $newDataArray[$key] = $value;\r\n        }\r\n    }\r\n    $dataArray = $newDataArray;\r\n}\r\n// We only enter update mode if we already have a valid formHashKey (tested above)\r\n// AND the updateSavedForm param was set to a truth-y value.\r\n$mode = ($updateSavedForm && $formHashKey) ? ''update'' : ''create'';\r\n// Create/get obj\r\n$newForm = null;\r\nif ($mode === ''update'') {\r\n    $newForm = $modx->getObject(''FormItForm'', array(''hash'' => $formHashKey));\r\n}\r\nif ($newForm === null) $newForm = $modx->newObject(''FormItForm'');\r\n\r\n// Handle encryption\r\nif($formEncrypt){\r\n    $dataArray = $newForm->encrypt($modx->toJSON($dataArray));\r\n}else{\r\n    $dataArray = $modx->toJSON($dataArray);\r\n}\r\n\r\n// Create new hash key on create mode, and handle invalid hash keys. \r\nif ($mode === ''create'') {\r\n    $formHashKey = ($formHashKeyRandom) ? $newForm->generatePseudoRandomHash() : pathinfo($formit->getStoreKey(), PATHINFO_BASENAME);\r\n}\r\n\r\n// Array from which to populate form record\r\n$newFormArray = array();\r\n\r\n// Special case: if updateSavedForm has the flag ''values'' we only merge in\r\n// the form values, not the other stuff\r\nif ($mode === ''update'' && $updateSavedForm === ''values'') {\r\n    $newFormArray = $newForm->toArray();\r\n    $newFormArray = array_merge($newFormArray, array(\r\n        ''values'' => $dataArray,\r\n    ));       \r\n} else {\r\n    // In all other cases, we overwrite the record completely!\r\n    // In create mode we must save the hash. In update mode, the \r\n    // formHashKey will be valid so we can also save it, again.\r\n    $newFormArray = array(\r\n        ''form'' => $formName,\r\n        ''date'' => time(),\r\n        ''values'' => $dataArray,\r\n        ''ip'' => $modx->getOption(''REMOTE_ADDR'', $_SERVER, ''''),\r\n        ''context_key'' => $modx->resource->get(''context_key''),\r\n        ''encrypted'' => $formEncrypt,\r\n        ''hash'' => $formHashKey,\r\n    );\r\n}\r\n// Convert to object\r\n$newForm->fromArray($newFormArray);\r\n// Attempt to save\r\nif (!$newForm->save()) {\r\n    $modx->log(modX::LOG_LEVEL_ERROR, ''[FormItSaveForm] An error occurred while trying to save the submitted form: '' . print_r($newForm->toArray(), true));\r\n    return false;\r\n}\r\n// Pass the hash and form data back to the user\r\n$hook->setValue(''savedForm'', $newForm->toArray());\r\n$hook->setValue($formHashKeyField, $newForm->get(''hash''));\r\nreturn true;', 0, 'a:0:{}', '', 0, ''),
(20, 0, 0, 'FormItLoadSavedForm', 'Prehook to load previously saved form.', 0, 3, 0, '/**\r\n * FormIt\r\n *\r\n * Copyright 2011-12 by SCHERP Ontwikkeling <info@scherpontwikkeling.nl>\r\n * Copyright 2015 by Wieger Sloot <modx@sterc.nl>\r\n * Copyright 2016 by YJ Tso <yj@modx.com>\r\n *\r\n * FormIt is free software; you can redistribute it and/or modify it\r\n * under the terms of the GNU General Public License as published by the Free\r\n * Software Foundation; either version 2 of the License, or (at your option) any\r\n * later version.\r\n *\r\n * FormIt is distributed in the hope that it will be useful, but WITHOUT ANY\r\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\r\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\r\n *\r\n * You should have received a copy of the GNU General Public License along with\r\n * FormIt; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\r\n * Suite 330, Boston, MA 02111-1307 USA\r\n *\r\n * @package formit\r\n */\r\n/**\r\n * A custom FormIt prehook for fetching saved form data. - Based on FormItSaveForm\r\n *\r\n * @var modX $modx\r\n * @var array $scriptProperties\r\n * @var FormIt $formit\r\n * @var fiHooks $hook\r\n * \r\n * @package formit\r\n */\r\n/* setup default properties */\r\n// If prehook fails do we continue?\r\n$return = $modx->getOption(''returnValueOnFail'', $formit->config, true);\r\n$formEncrypt = $modx->getOption(''formEncrypt'', $formit->config, false);\r\n$formFields = $modx->getOption(''formFields'', $formit->config, false);\r\n$fieldNames = $modx->getOption(''fieldNames'', $formit->config, false);\r\n$updateSavedForm = $modx->getOption(''updateSavedForm'', $formit->config, false); // true, false, ''1'', ''0'', or ''values''\r\n// If FormIt config says don''t update, don''t do it\r\nif (!$updateSavedForm) return $return;\r\n// In order to load form values, the user must provide the hash key somehow\r\n// Usually with a $_GET parameter, but a property in $formit->config will override.\r\n$formHashKeyField = $modx->getOption(''savedFormHashKeyField'', $formit->config, ''savedFormHashKey'');\r\n$formHashKey = '''';\r\nif (isset($_GET[$formHashKeyField])) $formHashKey = (string) $_GET[$formHashKeyField];\r\nif ($hook->getValue($formHashKeyField)) $formHashKey = (string) $hook->getValue($formHashKeyField);\r\nif (isset($formit->config[$formHashKeyField])) $formHashKey = $formit->config[$formHashKeyField];\r\n// our hashing methods return 32 chars. if no valid hash key we''re done here.\r\nif (strlen($formHashKey) !== 32) return $return;\r\n\r\n// Try to fetch the saved form\r\n$savedForm = $modx->getObject(''FormItForm'', array(''hash'' => $formHashKey));\r\nif (!$savedForm) return $return;\r\n\r\nif ($formFields) {\r\n    $formFields = explode('','', $formFields);\r\n    foreach($formFields as $k => $v) {\r\n        $formFields[$k] = trim($v);\r\n    }\r\n}\r\n\r\n// Initialize the data array\r\n// Handle encryption\r\nif ($formEncrypt) {\r\n    $data = $savedForm->decrypt();\r\n} else {\r\n    $data = $savedForm->get(''values'');\r\n}\r\nif (is_string($data)) $data = $modx->fromJSON($data);\r\nif (!is_array($data)) return $return;\r\n\r\n//Change the fieldnames\r\nif ($fieldNames) {\r\n    $newDataArray = array();\r\n    $fieldLabels = array();\r\n    $formFieldNames = explode('','', $fieldNames);\r\n    foreach($formFieldNames as $formFieldName){\r\n        list($name, $label) = explode(''=='', $formFieldName);\r\n        // reverse order from FormItSaveForm snippet\r\n        $fieldLabels[trim($label)] = trim($name);\r\n    }\r\n    foreach ($data as $key => $value) {\r\n        if ($fieldLabels[$key]) {\r\n            $newDataArray[$fieldLabels[$key]] = $value;\r\n        }else{\r\n            $newDataArray[$key] = $value;\r\n        }\r\n    }\r\n    $data = $newDataArray;\r\n}\r\n\r\n// Always pass back in the provided hash key\r\n$data[$formHashKeyField] = $formHashKey;\r\n$hook->setValues($data);\r\nreturn true;', 0, 'a:0:{}', '', 0, ''),
(61, 0, 0, 'phpthumbof', 'A custom output filter that generates thumbnails securely with phpThumb.', 0, 0, 0, '/**\n * phpThumbOf\n *\n * Copyright 2009-2012 by Shaun McCormick <shaun@modx.com>\n *\n * phpThumbOf is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * phpThumbOf is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * phpThumbOf; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package phpthumbof\n */\n/**\n * A custom output filter for phpThumb\n *\n * @var modX $modx\n * @var array $scriptProperties\n * @var phpThumbOf $phpThumbOf\n * @var string $input\n * @var string|array $options\n *\n * @package phpthumbof\n */\nif (empty($modx)) return '''';\nif (!$modx->loadClass(''modPhpThumb'',$modx->getOption(''core_path'').''model/phpthumb/'',true,true)) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''[phpThumbOf] Could not load modPhpThumb class.'');\n    return '''';\n}\nif (empty($input)) {\n    $modx->log(modX::LOG_LEVEL_DEBUG,''[phpThumbOf] Empty image path passed, aborting.'');\n    return '''';\n}\n$modelPath = $modx->getOption(''phpthumbof.core_path'',null,$modx->getOption(''core_path'').''components/phpthumbof/'').''model/'';\nrequire_once $modelPath.''phpthumbof/phpthumbof.class.php'';\n$phpThumbOf = new phpThumbOf($modx,$scriptProperties);\n\n$phpThumbOf->getCacheDirectory();\n$phpThumbOf->ensureCacheDirectoryIsWritable();\n\n$thumbnail = $phpThumbOf->createThumbnailObject();\n$thumbnail->setInput($input);\n$thumbnail->setOptions($options);\n$thumbnail->initializeService();\nreturn $thumbnail->render();', 0, NULL, '', 0, '');
INSERT INTO `modx_site_snippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`, `static`, `static_file`) VALUES
(22, 0, 0, 'getImageList', '', 0, 6, 0, '/**\n * getImageList\n *\n * Copyright 2009-2014 by Bruno Perner <b.perner@gmx.de>\n *\n * getImageList is free software; you can redistribute it and/or modify it\n * under the terms of the GNU General Public License as published by the Free\n * Software Foundation; either version 2 of the License, or (at your option) any\n * later version.\n *\n * getImageList is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * getImageList; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package migx\n */\n/**\n * getImageList\n *\n * display Items from outputvalue of TV with custom-TV-input-type MIGX or from other JSON-string for MODx Revolution \n *\n * @version 1.4\n * @author Bruno Perner <b.perner@gmx.de>\n * @copyright Copyright &copy; 2009-2014\n * @license http://www.gnu.org/licenses/old-licenses/gpl-2.0.html GNU General Public License\n * version 2 or (at your option) any later version.\n * @package migx\n */\n\n/*example: <ul>[[!getImageList? &tvname=`myTV`&tpl=`@CODE:<li>[[+idx]]<img src="[[+imageURL]]"/><p>[[+imageAlt]]</p></li>`]]</ul>*/\n/* get default properties */\n\n\n$tvname = $modx->getOption(''tvname'', $scriptProperties, '''');\n$tpl = $modx->getOption(''tpl'', $scriptProperties, '''');\n$wrapperTpl = $modx->getOption(''wrapperTpl'', $scriptProperties, '''');\n$limit = $modx->getOption(''limit'', $scriptProperties, ''0'');\n$offset = $modx->getOption(''offset'', $scriptProperties, 0);\n$totalVar = $modx->getOption(''totalVar'', $scriptProperties, ''total'');\n$randomize = $modx->getOption(''randomize'', $scriptProperties, false);\n$preselectLimit = $modx->getOption(''preselectLimit'', $scriptProperties, 0); // when random preselect important images\n$where = $modx->getOption(''where'', $scriptProperties, '''');\n$where = !empty($where) ? $modx->fromJSON($where) : array();\n$sort = $modx->getOption(''sort'', $scriptProperties, '''');\n$sort = !empty($sort) ? $modx->fromJSON($sort) : array();\n$toSeparatePlaceholders = $modx->getOption(''toSeparatePlaceholders'', $scriptProperties, false);\n$toPlaceholder = $modx->getOption(''toPlaceholder'', $scriptProperties, false);\n$outputSeparator = $modx->getOption(''outputSeparator'', $scriptProperties, '''');\n$splitSeparator = $modx->getOption(''splitSeparator'', $scriptProperties, '''');\n$placeholdersKeyField = $modx->getOption(''placeholdersKeyField'', $scriptProperties, ''MIGX_id'');\n$toJsonPlaceholder = $modx->getOption(''toJsonPlaceholder'', $scriptProperties, false);\n$jsonVarKey = $modx->getOption(''jsonVarKey'', $scriptProperties, ''migx_outputvalue'');\n$outputvalue = $modx->getOption(''value'', $scriptProperties, '''');\n$outputvalue = isset($_REQUEST[$jsonVarKey]) ? $_REQUEST[$jsonVarKey] : $outputvalue;\n$docidVarKey = $modx->getOption(''docidVarKey'', $scriptProperties, ''migx_docid'');\n$docid = $modx->getOption(''docid'', $scriptProperties, (isset($modx->resource) ? $modx->resource->get(''id'') : 1));\n$docid = isset($_REQUEST[$docidVarKey]) ? $_REQUEST[$docidVarKey] : $docid;\n$processTVs = $modx->getOption(''processTVs'', $scriptProperties, ''1'');\n$reverse = $modx->getOption(''reverse'', $scriptProperties, ''0'');\n$sumFields = $modx->getOption(''sumFields'', $scriptProperties, '''');\n$sumPrefix = $modx->getOption(''sumPrefix'', $scriptProperties, ''summary_'');\n$addfields = $modx->getOption(''addfields'', $scriptProperties, '''');\n$addfields = !empty($addfields) ? explode('','', $addfields) : null;\n//split json into parts\n$splits = $modx->fromJson($modx->getOption(''splits'', $scriptProperties, 0));\n$splitTpl = $modx->getOption(''splitTpl'', $scriptProperties, '''');\n$splitSeparator = $modx->getOption(''splitSeparator'', $scriptProperties, '''');\n$inheritFrom = $modx->getOption(''inheritFrom'', $scriptProperties, ''''); //commaseparated list of resource-ids or/and the keyword ''parents'' where to inherit from\n$inheritFrom = !empty($inheritFrom) ? explode('','',$inheritFrom) : '''';\n\n$modx->setPlaceholder(''docid'', $docid);\n\n$base_path = $modx->getOption(''base_path'', null, MODX_BASE_PATH);\n$base_url = $modx->getOption(''base_url'', null, MODX_BASE_URL);\n\n$migx = $modx->getService(''migx'', ''Migx'', $modx->getOption(''migx.core_path'', null, $modx->getOption(''core_path'') . ''components/migx/'') . ''model/migx/'', $scriptProperties);\nif (!($migx instanceof Migx))\n    return '''';\n$migx->working_context = isset($modx->resource) ? $modx->resource->get(''context_key'') : ''web'';\n\nif (!empty($tvname)) {\n    if ($tv = $modx->getObject(''modTemplateVar'', array(''name'' => $tvname))) {\n\n        /*\n        *   get inputProperties\n        */\n\n\n        $properties = $tv->get(''input_properties'');\n        $properties = isset($properties[''formtabs'']) ? $properties : $tv->getProperties();\n\n        $migx->config[''configs''] = $modx->getOption(''configs'', $properties, '''');\n        if (!empty($migx->config[''configs''])) {\n            $migx->loadConfigs();\n            // get tabs from file or migx-config-table\n            $formtabs = $migx->getTabs();\n        }\n        if (empty($formtabs) && isset($properties[''formtabs''])) {\n            //try to get formtabs and its fields from properties\n            $formtabs = $modx->fromJSON($properties[''formtabs'']);\n        }\n\n        if (!empty($properties[''basePath''])) {\n            if ($properties[''autoResourceFolders''] == ''true'') {\n                $scriptProperties[''base_path''] = $base_path . $properties[''basePath''] . $docid . ''/'';\n                $scriptProperties[''base_url''] = $base_url . $properties[''basePath''] . $docid . ''/'';\n            } else {\n                $scriptProperties[''base_path''] = $base_path . $properties[''base_path''];\n                $scriptProperties[''base_url''] = $base_url . $properties[''basePath''];\n            }\n        }\n        if ($jsonVarKey == ''migx_outputvalue'' && !empty($properties[''jsonvarkey''])) {\n            $jsonVarKey = $properties[''jsonvarkey''];\n            $outputvalue = isset($_REQUEST[$jsonVarKey]) ? $_REQUEST[$jsonVarKey] : $outputvalue;\n        }\n     \n        if (empty($outputvalue)){\n            $outputvalue = $tv->renderOutput($docid);\n            if (empty($outputvalue) && !empty($inheritFrom)){\n                foreach ($inheritFrom as $from){\n                    if ($from == ''parents''){\n                        $outputvalue = $tv->processInheritBinding('''',$docid);\n                    }else{\n                        $outputvalue = $tv->renderOutput($from);\n                    }\n                    if (!empty($outputvalue)){\n                        break;\n                    }                    \n                }\n            }\n        }\n\n       \n        /*\n        *   get inputTvs \n        */\n        $inputTvs = array();\n        if (is_array($formtabs)) {\n\n            //multiple different Forms\n            // Note: use same field-names and inputTVs in all forms\n            $inputTvs = $migx->extractInputTvs($formtabs);\n        }\n        if ($migx->source = $tv->getSource($migx->working_context, false)){\n            $migx->source->initialize();\n        }\n        \n    }\n\n\n}\n\nif (empty($outputvalue)) {\n    return '''';\n}\n\n//echo $outputvalue.''<br/><br/>'';\n\n$items = $modx->fromJSON($outputvalue);\n\n// where filter\nif (is_array($where) && count($where) > 0) {\n    $items = $migx->filterItems($where, $items);\n}\n$modx->setPlaceholder($totalVar, count($items));\n\nif (!empty($reverse)) {\n    $items = array_reverse($items);\n}\n\n// sort items\nif (is_array($sort) && count($sort) > 0) {\n    $items = $migx->sortDbResult($items, $sort);\n}\n\n$summaries = array();\n$output = '''';\n$items = $offset > 0 ? array_slice($items, $offset) : $items;\n$count = count($items);\n\nif ($count > 0) {\n    $limit = $limit == 0 || $limit > $count ? $count : $limit;\n    $preselectLimit = $preselectLimit > $count ? $count : $preselectLimit;\n    //preselect important items\n    $preitems = array();\n    if ($randomize && $preselectLimit > 0) {\n        for ($i = 0; $i < $preselectLimit; $i++) {\n            $preitems[] = $items[$i];\n            unset($items[$i]);\n        }\n        $limit = $limit - count($preitems);\n    }\n\n    //shuffle items\n    if ($randomize) {\n        shuffle($items);\n    }\n\n    //limit items\n    $count = count($items);\n    $tempitems = array();\n\n    for ($i = 0; $i < $limit; $i++) {\n        if ($i >= $count) {\n            break;\n        }\n        $tempitems[] = $items[$i];\n    }\n    $items = $tempitems;\n\n    //add preselected items and schuffle again\n    if ($randomize && $preselectLimit > 0) {\n        $items = array_merge($preitems, $items);\n        shuffle($items);\n    }\n\n    $properties = array();\n    foreach ($scriptProperties as $property => $value) {\n        $properties[''property.'' . $property] = $value;\n    }\n\n    $idx = 0;\n    $output = array();\n    $template = array();\n    $count = count($items);\n\n    foreach ($items as $key => $item) {\n        $formname = isset($item[''MIGX_formname'']) ? $item[''MIGX_formname''] . ''_'' : '''';\n        $fields = array();\n        foreach ($item as $field => $value) {\n            if (is_array($value)) {\n                if (is_array($value[0])) {\n                    //nested array - convert to json\n                    $value = $modx->toJson($value);\n                } else {\n                    $value = implode(''||'', $value); //handle arrays (checkboxes, multiselects)\n                }\n            }\n\n\n            $inputTVkey = $formname . $field;\n\n            if ($processTVs && isset($inputTvs[$inputTVkey])) {\n                if (isset($inputTvs[$inputTVkey][''inputTV'']) && $tv = $modx->getObject(''modTemplateVar'', array(''name'' => $inputTvs[$inputTVkey][''inputTV'']))) {\n\n                } else {\n                    $tv = $modx->newObject(''modTemplateVar'');\n                    $tv->set(''type'', $inputTvs[$inputTVkey][''inputTVtype'']);\n                }\n                $inputTV = $inputTvs[$inputTVkey];\n\n                $mTypes = $modx->getOption(''manipulatable_url_tv_output_types'', null, ''image,file'');\n                //don''t manipulate any urls here\n                $modx->setOption(''manipulatable_url_tv_output_types'', '''');\n                $tv->set(''default_text'', $value);\n                $value = $tv->renderOutput($docid);\n                //set option back\n                $modx->setOption(''manipulatable_url_tv_output_types'', $mTypes);\n                //now manipulate urls\n                if ($mediasource = $migx->getFieldSource($inputTV, $tv)) {\n                    $mTypes = explode('','', $mTypes);\n                    if (!empty($value) && in_array($tv->get(''type''), $mTypes)) {\n                        //$value = $mediasource->prepareOutputUrl($value);\n                        $value = str_replace(''/./'', ''/'', $mediasource->prepareOutputUrl($value));\n                    }\n                }\n\n            }\n            $fields[$field] = $value;\n\n        }\n\n        if (!empty($addfields)) {\n            foreach ($addfields as $addfield) {\n                $addfield = explode('':'', $addfield);\n                $addname = $addfield[0];\n                $adddefault = isset($addfield[1]) ? $addfield[1] : '''';\n                $fields[$addname] = $adddefault;\n            }\n        }\n\n        if (!empty($sumFields)) {\n            $sumFields = is_array($sumFields) ? $sumFields : explode('','', $sumFields);\n            foreach ($sumFields as $sumField) {\n       