(2, 1, 0, 'Главная', '', 0, 4, '', 0, '[[$head]]\n[[$header]]\n[[$forma_zvonka]]\n[[*id:is=`1`:then=`[[$slider]]`]]\n<div class="section group">\n    [[pdoResources?\n    	&tpl=`articles`\n    	&limit=`5`\n    	&parents=`44`\n    	&includeContent=`1`\n    ]]\n</div>\n\n\n<div class="grids">\n\n	<h3 class="black">Популярные товары и акции</h3>\n	\n[[pdoResources?\n	&tpl=`product_main`\n	&limit=`6`\n	&parents=`4`\n	&includeContent=`1`\n	&includeTVs=`galleryPhotos,price,oldprice,top,share,discount`\n	&tvFilters=`top==1||share==1||discount==1`\n	&hideContainers=`1`\n]]\n<div class="clear"></div>\n    <h3 class="black">Новые поступления</h3>\n	\n[[pdoResources?\n	&tpl=`product_main`\n	&limit=`6`\n	&parents=`4`\n	&includeContent=`1`\n	&includeTVs=`galleryPhotos,price,oldprice,top,share,discount`\n	&hideContainers=`1`\n	&where=`{"template:IN":[ 3,6,7,12,13,14 ]}`\n]]\n	\n\n\n</div>		\n<div class="slidebar">\n<h4>Наши новости</h4>\n\n[[pdoResources?\n	&tpl=`news`\n	&limit=`4`\n	&parents=`22`\n	&includeContent=`1`\n]]\n\n</div>\n<div class="clear"> </div>\n	[[$footer]]', 0, 'a:0:{}', 0, ''),
(3, 1, 0, 'Карточка_освещение', '', 0, 4, '', 0, '[[$head]]\n[[$header]]\n[[$forma_zvonka]]\n[[pdoCrumbs?\n	&tplWrapper=`@INLINE <ul class="breadcrumbs">[[+output]]</ul>`\n	&tplHome=`@INLINE <li><a href="/">Главная</a></li>`\n	&showHome=`1`\n]]\n\n[[$sidebar_left]]\n\n<article itemscope itemtype="http://schema.org/Product">\n	<h3 class="black" itemprop="name">[[*pagetitle]]</h3>	\n    <p class="product-descr" itemprop="description">[[*description]]</p>\n	<div class="product-photo">\n	    [[getImageList?\n            &tvname=`galleryPhotos`\n            &tpl=`foto_small`\n            &limit=`30`\n            &docid=`[[*id]]`\n          ]]\n	</div>\n\n 	<div class="product-info" itemprop="offers" itemscope itemtype="http://schema.org/Offer">\n 		<h3 class="product-title">[[*longtitle]]</h3>\n\n		<span class="old-price">[[*oldprice]]</span><br>\n		<span class="new-price" itemprop="price">[[*price]]</span><br><br>\n<!--В поле priceCurrency указывается валюта.-->\n    <span itemprop="priceCurrency" style="display:none">UAH</span>\n					\n					\n<div class="product shk-item col-sm-6 col-md-4">\n    <form action="[[~[[*id]]? &scheme=`abs`]]" method="post">\n        <fieldset>\n            <input type="hidden" name="shk-id" value="[[*id]]" />\n            <input type="hidden" name="shk-count" value="1" />\n            <div class="product-price pull_right">\n                <button type="submit" class="shk-but buy-goods-button">Купить</button>\n                <!--<div>Цена: <span class="shk-price">[[-*price:num_format]]</span> руб.</div>-->\n            </div>\n        </fieldset>\n    </form>\n</div>\n<br>\n					\n			\n	</div>  \n\n				<div class="clear"></div>\n				<br>\n\n				<!--<div class="pull_right"><a href="#" class="buy-goods-button">купить</a></div>-->\n\n				<div class="clear"></div>\n\n			\n			<br>\n<script type="text/javascript">\n\n$(document).ready(function() {\n\n	//Default Action\n	$(".tab_content").hide(); //Hide all content\n	$("ul.tabs li:first").addClass("active").show(); //Activate first tab\n	$(".tab_content:first").show(); //Show first tab content\n\n	//On Click Event\n	$("ul.tabs li").click(function() {\n		$("ul.tabs li").removeClass("active"); //Remove any "active" class\n		$(this).addClass("active"); //Add "active" class to selected tab\n		$(".tab_content").hide(); //Hide all tab content\n		var activeTab = $(this).find("a").attr("href"); //Find the rel attribute value to identify the active tab + content\n		$(activeTab).fadeIn(); //Fade in the active content\n		return false;\n	});\n\n});\n</script>\n<div class="containerss">\n\n    <ul class="tabs">\n        <li><a href="#tab1">Описание товара</a></li>\n        <li><a href="#tab2">Характеристики</a></li>\n        <li><a href="#tab3">Видео обзоры</a></li>\n        <!--<li><a href="#tab4">Отзывы покупателей</a></li>-->\n\n    </ul>\n    <div class="tab_container">\n        <div id="tab1" class="tab_content">[[*content]]</div>\n        <div id="tab2" class="tab_content">\n          \n            \n  \n<table class="simple-little-table" cellspacing=''0''>\n<tr>\n\n</tr><!-- Table Header -->\n    \n\n[[*light_type:notempty=`<tr><td>Тип света фар</td>\n<td>[[*light_type]]</td></tr>`]]\n\n[[*diod_type:notempty=`<tr><td>Тип светодиодов</td>\n<td>[[*diod_type]]</td></tr>`]]\n\n[[*potok:notempty=`<tr><td>Световой поток/td>\n<td>[[*potok]]</td></tr>`]]\n\n[[*power:notempty=`<tr><td>Мощность/td>\n<td>[[*power]]</td></tr>`]]\n\n[[*tok:notempty=`<tr><td>Потребление тока/td>\n<td>[[*tok]]</td></tr>`]]\n\n[[*voltage:notempty=`<tr><td>Напряжение питания/td>\n<td>[[*voltage]]</td></tr>`]]\n\n[[*vendor:notempty=`<tr><td>Производитель</td>\n<td>[[*vendor]]</td></tr>`]]\n\n[[*country:notempty=`<tr><td>Страна производства</td>\n<td>[[*country]]</td></tr>`]]\n\n[[*options:notempty=`<tr><td>Опции</td>\n<td>[[*options]]</td></tr>`]]\n\n[[*color_reflector:notempty=`<tr><td>Цвет отражателя</td>\n<td>[[*color_reflector]]</td></tr>`]]\n\n[[*color_glass:notempty=`<tr><td>Цвет стекла</td>\n<td>[[*color_glass]]</td></tr>`]]\n\n[[*length:notempty=`<tr><td>Длина</td>\n<td>[[*length]]</td></tr>`]]\n\n[[*width:notempty=`<tr><td>Ширина</td>\n<td>[[*width]]</td></tr>`]]\n\n[[*height:notempty=`<tr><td>Высота</td>\n<td>[[*height]]</td></tr>`]]\n\n[[*diameter:notempty=`<tr><td>Диаметр</td>\n<td>[[*diameter]]</td></tr>`]]\n\n[[*ved_chislo:notempty=`<tr><td>Ведущее число</td>\n<td>[[*ved_chislo]]</td></tr>`]]\n\n[[*weight:notempty=`<tr><td>Вес</td>\n<td>[[*weight]]</td></tr>`]]\n\n[[*color:notempty=`<tr><td>Цвет корпуса</td>\n<td>[[*color]]</td></tr>`]]\n\n[[*form:notempty=`<tr><td>Фома фар</td>\n<td>[[*form]]</td></tr>`]]\n\n[[*type_headlights:notempty=`<tr><td>Тип фар</td>\n<td>[[*type_headlights]]</td></tr>`]]\n\n[[*equipment:notempty=`<tr><td>Комплектация</td>\n<td>[[*equipment]]</td></tr>`]]\n\n[[*life_time:notempty=`<tr><td>Срок службы</td>\n<td>[[*life_time]]</td></tr>`]]\n\n[[*temperature:notempty=`<tr><td>Температура свечения (цветовая)</td>\n<td>[[*temperature]]</td></tr>`]]\n\n[[*class_ip:notempty=`<tr><td>Класс пыле-влагозащиты</td>\n<td>[[*class_ip]]</td></tr>`]]\n\n[[*quantity:notempty=`<tr><td>Количество светодиодов</td>\n<td>[[*quantity]]</td></tr>`]]\n\n[[*lum_angle:notempty=`<tr><td>Угол светового потока</td>\n<td>[[*lum_angle]]</td></tr>`]]\n\n</table>\n\n \n\n        </div>\n        <div id="tab3" class="tab_content">[[*video]]</div>\n        <!--<div id="tab4" class="tab_content"></div>-->\n    </div>\n</div>\n[[*class_key:is=`SelectionContainer`:then=`<h3 class="black">Рекомендуемые товары</h3>`]]	\n[[getSelections? \n	&selections=`[[*id]]` \n	&tpl=`productRel`\n	&getResourcesSnippet=`pdoResources`\n	&includeTVs=`price,top,share,discount`\n]]\n\n</article>			\n\n[[$footer]]\n', 0, 'a:0:{}', 0, ''),
(9, 1, 0, 'Оформление заказа', '', 0, 4, '', 0, '[[$head]]\n[[$header]]\n[[$forma_zvonka]]\n[[pdoCrumbs?\n	&tplWrapper=`@INLINE <ul class="breadcrumbs">[[+output]]</ul>`\n	&tplHome=`@INLINE <li><a href="/">Главная</a></li>`\n	&showHome=`1`\n]]\n<article class="full">\n	<h3 class="black">[[*pagetitle]]</h3>	\n\n[[!Shopkeeper3@cart_order_page]]\n\n[[!FormIt?\n&hooks=`spam,shk_fihook,email,FormItAutoResponder,redirect`\n&submitVar=`order`\n&emailTpl=`shopOrderReport`\n&fiarTpl=`shopOrderReport`\n&emailSubject=`В интернет-магазине "[[++site_name]]" сделан новый заказ`\n&fiarSubject=`Cделан новый заказ в интернет-магазине "[[++site_name]]"`\n&emailTo=`[[++emailsender]]`\n&emailFromName=`[[++site_name]]`\n&emailFrom=`[[++emailsender]]`\n&fiarReplyTo=`[[++emailsender]]`\n&fiarFromName=`[[++site_name]]`\n&fiarToField=`email`\n&redirectTo=`21`\n&validate=`address:required,fullname:required,email:email:required,phone:required`\n&errTpl=`<br /><span class="error">[[+error]]</span>`\n]]\n\n[[$shopOrderForm_new]]\n\n\n</article>\n[[$footer]]', 0, 'a:0:{}', 0, ''),
